    <h1 class="font-bold text-2xl">Target your respondents</h1>
    <p class="mx-14 mt-4 text-center">Refine your survey's reach with precision. "Target your respondents" enables you
        to tailor your questions to specific demographics, ensuring you gather the most relevant and valuable insights
        from your desired audience.</p>

    <div class="w-full mt-6 mx-12">
        <div wire:click="$set('target', 'all')">
            <x-forms.multiple-choice name="I want to target all" />
        </div>
        <div class="mt-2"></div>
        <div wire:click="$set('target', 'individual')">
            <x-forms.multiple-choice name="I want to target individually" />
        </div>
        @if ($this->target == 'individual')
            <div class="mt-4">
                <nav class="flex border-b border-gray-100 text-sm font-medium mb-5 w-full">
                    <button wire:click="$set('tab', 'groups')"
                        class="-mb-px grow border-b {{ $tab == 'groups' ? 'border-current text-indigo-500' : 'border-transparent hover:text-indigo-500' }} p-4">
                        Groups
                    </button>

                    <button wire:click="$set('tab', 'departments')"
                        class="-mb-px grow border-b {{ $tab == 'departments' ? 'border-current text-indigo-500' : 'border-transparent hover:text-indigo-500' }} p-4">
                        Departments
                    </button>

                    <button wire:click="$set('tab', 'individual')"
                        class="-mb-px grow border-b {{ $tab == 'individual' ? 'border-current text-indigo-500' : 'border-transparent hover:text-indigo-500' }} p-4">
                        Individual
                    </button>
                </nav>
            </div>

            <div class="mt-4">
                @switch($this->tab)
                @case('groups')
                    <livewire:group-dropdown />
                    <div class="grid grid-cols-12 gap-6 mt-4">
                        @foreach(App\Models\Group::all() as $member)
                        <x-community.groups-tiles-cards :member="$member" />
                        @endforeach
                    </div>
                    @break
                    @case('departments')
                    <livewire:department-dropdown />
                    <div class="grid grid-cols-12 gap-6 mt-4">
                        @foreach(App\Models\Department::all() as $member)
                        <x-community.groups-tiles-cards :member="$member" />
                        @endforeach
                    </div>
                    @break
                    @case('individual')
                    <h1>Individual</h1>
                    @break
            @endswitch
            </div>
        @endif

       

